// Fitnes1.jsx
import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Fitnes1.css';
import { HomeOutlined, DashboardOutlined, CalendarOutlined, UserOutlined } from '@ant-design/icons';
import * as posenet from '@tensorflow-models/posenet';
import '@tensorflow/tfjs';

const Fitnes1 = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const [net, setNet] = useState(null);

  useEffect(() => {
    const loadPosenet = async () => {
      const net = await posenet.load();
      setNet(net);
    };
    loadPosenet();
  }, []);

  useEffect(() => {
    if (isCameraOpen) {
      startCamera();
    } else {
      stopCamera();
    }
  }, [isCameraOpen]);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      videoRef.current.onloadedmetadata = () => {
        videoRef.current.play();
        detectPose();
      };
    } catch (err) {
      console.error("Error accessing the camera: ", err);
      alert("カメラへのアクセスが拒否されました。ブラウザの設定を確認してください。");
    }
  };

  const stopCamera = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }
  };

  const detectPose = async () => {
    if (!net || !isCameraOpen) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const poseDetectionFrame = async () => {
      const pose = await net.estimateSinglePose(video, {
        flipHorizontal: false,
      });

      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

      drawKeypoints(pose['keypoints'], 0.6, ctx);
      drawSkeleton(pose['keypoints'], 0.7, ctx);

      if (isCameraOpen) {
        requestAnimationFrame(poseDetectionFrame);
      }
    };

    poseDetectionFrame();
  };

  const drawKeypoints = (keypoints, minConfidence, ctx) => {
    keypoints.forEach(keypoint => {
      if (keypoint.score >= minConfidence) {
        const { y, x } = keypoint.position;
        ctx.beginPath();
        ctx.arc(x, y, 5, 0, 2 * Math.PI);
        ctx.fillStyle = 'aqua';
        ctx.fill();
      }
    });
  };

  const drawSkeleton = (keypoints, minConfidence, ctx) => {
    const adjacentKeyPoints = posenet.getAdjacentKeyPoints(keypoints, minConfidence);

    adjacentKeyPoints.forEach(keypoints => {
      const [from, to] = keypoints;
      ctx.beginPath();
      ctx.moveTo(from.position.x, from.position.y);
      ctx.lineTo(to.position.x, to.position.y);
      ctx.lineWidth = 2;
      ctx.strokeStyle = 'aqua';
      ctx.stroke();
    });
  };

  return (
    <div className="fitnes1-container">
      {/* ... */}
      <div className={`video-container ${isCameraOpen ? 'camera-open' : ''}`}>
        {isCameraOpen ? (
          <div className="camera-feed">
            <video ref={videoRef} style={{ width: '100%', display: 'none' }} autoPlay muted />
            <canvas ref={canvasRef} style={{ width: '100%', height: '100%' }} />
            <button className="stop-button" onClick={() => setIsCameraOpen(false)}>
              撮影を停止する
            </button>
          </div>
        ) : (
          <button className="start-button" onClick={() => setIsCameraOpen(true)}>
            撮影を開始する
          </button>
        )}
      </div>
    </div>
  );
};

export default Fitnes1;
