import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaHome, FaDumbbell, FaUser, FaChartLine } from 'react-icons/fa';
import '../styles/Fitnes1.css';

const Fitnes1 = () => {
    const navigate = useNavigate();
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const [prediction, setPrediction] = useState(null);
    const [isCameraActive, setIsCameraActive] = useState(false);

    useEffect(() => {
        if (isCameraActive) {
            const startVideo = async () => {
                try {
                    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
                    videoRef.current.srcObject = stream;
                } catch (error) {
                    console.error('Error accessing the camera', error);
                }
            };
            startVideo();
        } else {
            const stream = videoRef.current?.srcObject;
            if (stream) {
                const tracks = stream.getTracks();
                tracks.forEach(track => track.stop());
                videoRef.current.srcObject = null;
            }
        }
    }, [isCameraActive]);

    useEffect(() => {
        if (isCameraActive) {
            const interval = setInterval(() => {
                captureFrame();
            }, 1000); // 1秒ごとにフレームをキャプチャ

            return () => clearInterval(interval);
        }
    }, [isCameraActive]);

    const captureFrame = async () => {
        const video = videoRef.current;
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        canvas.toBlob(async (blob) => {
            const formData = new FormData();
            formData.append('file', blob, 'frame.jpg');

            try {
                const response = await axios.post('http://localhost:5000/predict', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                });
                setPrediction(response.data);
            } catch (error) {
                console.error('Error sending frame to backend', error);
            }
        }, 'image/jpeg');
    };

    const handleNavigation = (path) => {
        navigate(path);
    };

    return (
        <div className="fitnes1-container">
            <div className="tab-bar">
                <div className="hamburger-icon">☰</div>
                <div className="profile-icon" onClick={() => handleNavigation('/profile')}>👤</div>
            </div>
            <div className="sidebar">
                <ul>
                    <li onClick={() => handleNavigation('/home')}><FaHome /> ホーム</li>
                    <li onClick={() => handleNavigation('/workouts')}><FaDumbbell /> ワークアウト</li>
                    <li onClick={() => handleNavigation('/progress')}><FaChartLine /> 進捗</li>
                    <li onClick={() => handleNavigation('/profile')}><FaUser /> プロフィール</li>
                </ul>
            </div>
            <h1>Yoro予測</h1>
            <div className="video-container">
                <div className="video-item">
                    <h2>カメラフィード</h2>
                    <div className="camera-feed">
                        <video ref={videoRef} autoPlay className="video" />
                        <canvas ref={canvasRef} width="640" height="480" className="canvas" />
                    </div>
                </div>
                {prediction && (
                    <div className="video-item">
                        <h2>予測結果</h2>
                        <pre>{JSON.stringify(prediction, null, 2)}</pre>
                    </div>
                )}
            </div>
            <div className="description">
                <p>説明文</p>
            </div>
            <button
                className={isCameraActive ? 'stop-button' : 'start-button'}
                onClick={() => setIsCameraActive(!isCameraActive)}
            >
                {isCameraActive ? 'カメラ停止' : 'カメラ開始'}
            </button>
            <a href="/" className="back-button">戻る</a>
            <div className="video-container">
                <div className="video-item">
                    <div className="video-wrapper">
                        <iframe width="560" height="315" src="https://www.youtube.com/embed/dQw4w9WgXcQ" title="YouTube video player" frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Fitnes1;
