import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Fitnes1.css';
import { HomeOutlined, DashboardOutlined, CalendarOutlined, UserOutlined } from '@ant-design/icons';
import io from 'socket.io-client';

const Fitnes1 = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const streamRef = useRef(null);
  const socketRef = useRef(null);
  const lastProcessedTime = useRef(0);
  const FPS = 30;
  const processingInterval = 1000 / FPS;

  useEffect(() => {
    if (isCameraOpen) {
      startCamera();
      connectSocket();
    } else {
      stopCamera();
      disconnectSocket();
    }

    return () => {
      stopCamera();
      disconnectSocket();
    };
  }, [isCameraOpen]);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: { width: 640, height: 480 } });
      videoRef.current.srcObject = stream;
      streamRef.current = stream;
      videoRef.current.onloadedmetadata = () => {
        videoRef.current.play();
        processFrame();
      };
    } catch (err) {
      console.error("Error accessing the camera: ", err);
      alert("カメラへのアクセスが拒否されました。ブラウザの設定を確認してください。");
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  const connectSocket = () => {
    socketRef.current = io('http://localhost:5000', {
      transports: ['websocket']
    });
    
    socketRef.current.on('connect', () => {
      console.log('Socket connected');
    });

    socketRef.current.on('processed_frame', (data) => {
      updateCanvas(data.processedFrame);
    });

    socketRef.current.on('error', (data) => {
      console.error('Server error:', data.error);
    });
  };

  const disconnectSocket = () => {
    if (socketRef.current) {
      socketRef.current.disconnect();
    }
  };

  const processFrame = () => {
    if (!isCameraOpen || !videoRef.current || !canvasRef.current) return;

    const now = Date.now();
    if (now - lastProcessedTime.current >= processingInterval) {
      const video = videoRef.current;
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');

      canvas.width = 640;
      canvas.height = 480;

      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
      const videoFrame = canvas.toDataURL('image/jpeg', 0.7);

      if (socketRef.current && socketRef.current.connected) {
        socketRef.current.emit('process_frame', { image: videoFrame });
        console.log('Frame sent to server');
      }

      lastProcessedTime.current = now;
    }

    requestAnimationFrame(processFrame);
  };

  const updateCanvas = (processedFrame) => {
    if (!canvasRef.current) return;
    console.log('Frame received from server');
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();
    img.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
    };
    img.src = processedFrame;
  };

  return (
    <div className="fitnes1-container">
      <div className="tab-bar">
        <div
          className={`hamburger-icon ${isSidebarOpen ? 'active' : ''}`}
          onClick={toggleSidebar}
        >
          &#9776;
        </div>
        <Link to="/profile" className="profile-icon">
          <UserOutlined />
        </Link>
      </div>
      <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <ul>
          <li><HomeOutlined /> Home</li>
          <li><DashboardOutlined /> Dashboard</li>
          <li><CalendarOutlined /> Calendar</li>
          <li><UserOutlined /> Profile</li>
        </ul>
      </div>
      <h1>首の可動性</h1>
      <div className="description">
        <p>長時間のデスクワークやスマホの操作で悪い姿勢(ストレートネックなど)になってしまい、</p>
        <p>首回りの筋肉が硬くなってる方も多いと思います。首回りの筋肉の硬さが習慣的になると</p>
        <p>頭痛や頭部痛の原因となります。セルフチェック動画を活用し、首の可動性を確認してみましょう。</p>
      </div>
      <div className="video-container">
        <div className="video-item">
          <h2>自分の身体知っていますか？</h2>
          <p>ー 整体広沼のセルフチェック ー</p>
          <div className="video-wrapper">
            <iframe
              width="560"
              height="315"
              src="https://www.youtube.com/embed/4-YHy0wMT2w"
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
        </div>
        <div className="video-item">
          <h2>自分の身体知っていますか？</h2>
          <p>ー 整体広沼の動作改善エクササイズ ー</p>
          <div className="video-wrapper">
            <iframe
              width="560"
              height="315"
              src="https://www.youtube.com/embed/fw0DQBt3psM"
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
        </div>
      </div>
      <div className={`video-container ${isCameraOpen ? 'camera-open' : ''}`}>
        {isCameraOpen ? (
          <div className="camera-feed-wrapper">
            <div className="camera-feed">
              <video ref={videoRef} style={{ display: 'none' }} autoPlay muted />
              <canvas ref={canvasRef} />
            </div>
            <button className="stop-button" onClick={() => setIsCameraOpen(false)}>
              撮影を停止する
            </button>
          </div>
        ) : (
          <button className="start-button" onClick={() => setIsCameraOpen(true)}>
            撮影を開始する
          </button>
        )}
      </div>
    </div>
  );
};

export default Fitnes1;
