import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Fitnes1.css';
import { HomeOutlined, DashboardOutlined, CalendarOutlined, UserOutlined } from '@ant-design/icons';
import axios from 'axios';

const Fitnes1 = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const [isComplete, setIsComplete] = useState(false);
  const streamRef = useRef(null);
  const detectFrameIdRef = useRef(null);

  useEffect(() => {
    if (isCameraOpen) {
      startCamera();
    } else {
      stopCamera();
    }

    return () => {
      stopCamera();
    };
  }, [isCameraOpen]);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      streamRef.current = stream;
      videoRef.current.onloadedmetadata = () => {
        videoRef.current.play();
        detectObjects();
      };
    } catch (err) {
      console.error("Error accessing the camera: ", err);
      alert("カメラへのアクセスが拒否されました。ブラウザの設定を確認してください。");
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    if (detectFrameIdRef.current) {
      cancelAnimationFrame(detectFrameIdRef.current);
      detectFrameIdRef.current = null;
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  const detectObjects = async () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const detectFrame = async () => {
      if (!isCameraOpen) {
        return;
      }

      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
      const videoFrame = canvas.toDataURL('image/jpeg', 0.8);

      try {
        const response = await axios.post('/api/detect', { image: videoFrame }, {
          headers: {
            'Content-Type': 'application/json',
          },
        });

        const { image, isRightHandRaised } = response.data;

        if (isRightHandRaised) {
          setIsComplete(true);
          stopCamera();
          return;
        }

        const img = new Image();
        img.onload = () => {
          ctx.clearRect(0, 0, canvas.width, canvas.height);
          ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
        };
        img.src = `data:image/jpeg;base64,${image}`;
      } catch (err) {
        console.error('Error processing frame:', err);
      }

      detectFrameIdRef.current = requestAnimationFrame(detectFrame);
    };

    detectFrameIdRef.current = requestAnimationFrame(detectFrame);
  };

  const saveSessionData = async () => {
    try {
      const response = await axios.post('/api/save_session');
      console.log(response.data.message);
      alert('セッションデータが保存されました。');
    } catch (error) {
      console.error('Error saving session data:', error);
      alert('セッションデータの保存中にエラーが発生しました。');
    }
  };

  return (
    <div className="fitnes1-container">
      <div className="tab-bar">
        <div
          className={`hamburger-icon ${isSidebarOpen ? 'active' : ''}`}
          onClick={toggleSidebar}
        >
          &#9776;
        </div>
        <Link to="/profile" className="profile-icon">
          <UserOutlined />
        </Link>
      </div>
      <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <ul>
          <li><HomeOutlined /> Home</li>
          <li><DashboardOutlined /> Dashboard</li>
          <li><CalendarOutlined /> Calendar</li>
          <li><UserOutlined /> Profile</li>
        </ul>
      </div>
      <h1>首の可動性 - 右手を上げる動作</h1>
      <div className="description">
        <p>このエクササイズでは、右手を上げる動作を行います。</p>
        <p>カメラの前に立ち、右手をゆっくりと上げてください。</p>
        <p>システムが正しい姿勢を検出すると、自動的に撮影が停止します。</p>
      </div>
      <div className="video-container">
        <div className="video-item">
          <h2>自分の身体知っていますか？</h2>
          <p>ー 整体広沼のセルフチェック ー</p>
          <div className="video-wrapper">
            <iframe
              width="560"
              height="315"
              src="https://www.youtube.com/embed/4-YHy0wMT2w"
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
        </div>
        <div className="video-item">
          <h2>自分の身体知っていますか？</h2>
          <p>ー 整体広沼の動作改善エクササイズ ー</p>
          <div className="video-wrapper">
            <iframe
              width="560"
              height="315"
              src="https://www.youtube.com/embed/fw0DQBt3psM"
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
        </div>
      </div>
      <div className={`video-container ${isCameraOpen ? 'camera-open' : ''}`}>
        {isComplete ? (
          <div className="complete-message">
            <h2>完了！</h2>
            <p>正しい姿勢が検出されました。お疲れ様でした。</p>
            <button onClick={() => {
              setIsCameraOpen(false);
              setIsComplete(false);
              saveSessionData();
            }}>
              データを保存して終了
            </button>
          </div>
        ) : isCameraOpen ? (
          <div className="camera-feed-wrapper">
            <div className="camera-feed">
              <video ref={videoRef} style={{ display: 'none' }} autoPlay muted />
              <canvas ref={canvasRef} />
            </div>
            <button className="stop-button" onClick={() => {
              setIsCameraOpen(false);
              saveSessionData();
            }}>
              撮影を停止してデータを保存
            </button>
          </div>
        ) : (
          <button className="start-button" onClick={() => setIsCameraOpen(true)}>
            撮影を開始する
          </button>
        )}
      </div>
    </div>
  );
};

export default Fitnes1;
