import React, { useEffect, useRef } from 'react';

const TestCameraAccess = () => {
  const videoRef = useRef(null);

  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        videoRef.current.srcObject = stream;
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play();
        };
      } catch (err) {
        console.error("Error accessing the camera: ", err.name, err.message);
        alert(`カメラへのアクセスが拒否されました。エラー: ${err.name} - ${err.message}`);
      }
    };

    startCamera();
  }, []);

  return (
    <div>
      <h1>Camera Access Test</h1>
      <video ref={videoRef} style={{ width: '100%' }} autoPlay muted />
    </div>
  );
};

export default TestCameraAccess;
